function initScrollAnimations(){const e=document.querySelectorAll(".fade-in, .slide-in, .zoom-in, .hero-title, .hero-subtitle, .section-title, .card, .service-item, .project-item, .team-member"),t=new IntersectionObserver(e=>{e.forEach(e=>{e.isIntersecting&&(e.target.classList.add("animated"),t.unobserve(e.target))})},{threshold:.1,rootMargin:"0px 0px -50px 0px"});e.forEach(e=>{e.classList.contains("fade-in")||e.classList.contains("slide-in")||e.classList.contains("zoom-in")||e.classList.add("fade-in"),t.observe(e)});document.querySelectorAll(".staggered-list").forEach(e=>{const t=e.children;Array.from(t).forEach((e,t)=>{e.style.animationDelay=.1*t+"s",e.classList.add("staggered-item")})})}function initHoverEffects(){document.querySelectorAll(".btn").forEach(e=>{e.addEventListener("mousemove",function(e){const t=this.getBoundingClientRect(),o=e.clientX-t.left,n=e.clientY-t.top;this.style.setProperty("--x-pos",`${o}px`),this.style.setProperty("--y-pos",`${n}px`)}),e.addEventListener("mouseleave",function(){this.style.setProperty("--x-pos","50%"),this.style.setProperty("--y-pos","50%")})});document.querySelectorAll(".card, .service-item, .project-item, .team-member").forEach(e=>{e.addEventListener("mouseenter",function(){this.classList.add("hover")}),e.addEventListener("mouseleave",function(){this.classList.remove("hover")})})}function initPageTransitions(){document.querySelectorAll('a[href^="#"]').forEach(e=>{e.addEventListener("click",function(e){e.preventDefault();const t=this.getAttribute("href");if("#"===t)return;const o=document.querySelector(t);o&&(document.querySelectorAll(".nav-link").forEach(e=>{e.classList.remove("active")}),this.classList.add("active"),o.scrollIntoView({behavior:"smooth",block:"start"}))})})}function updateAnimationsForTheme(e){const t=document.documentElement;"light"===e?t.style.setProperty("--animation-shadow-color","rgba(0, 0, 0, 0.1)"):"dark"===e?t.style.setProperty("--animation-shadow-color","rgba(245, 158, 11, 0.2)"):"blue"===e&&t.style.setProperty("--animation-shadow-color","rgba(56, 189, 248, 0.2)")}document.addEventListener("DOMContentLoaded",function(){window.matchMedia("(prefers-reduced-motion: reduce)").matches?document.body.classList.add("reduced-motion"):(initScrollAnimations(),initHoverEffects(),initPageTransitions()),document.addEventListener("themechange",function(e){updateAnimationsForTheme(e.detail.theme)})});